$OutputPath = "$env:USERPROFILE\Desktop\serial.txt"; if (-not (Test-Path (Split-Path $OutputPath))) { New-Item -ItemType Directory -Path (Split-Path $OutputPath) -Force }; $output = @(); $output += "`n=== DISK SERIAL ==="; Get-CimInstance Win32_DiskDrive | ForEach-Object { $output += $_.SerialNumber }; $output += "`n=== MOTHERBOARD SERIAL ==="; $output += (Get-CimInstance Win32_BaseBoard).SerialNumber; $output += "`n=== SMBIOS UUID ==="; $output += (Get-CimInstance Win32_ComputerSystemProduct).UUID; $output += "`n=== GPU ==="; Get-CimInstance Win32_VideoController | ForEach-Object { $output += "$($_.Description) - $($_.PNPDeviceID)" }; $output += "`n=== RAM SERIAL ==="; Get-CimInstance Win32_PhysicalMemory | ForEach-Object { $output += $_.SerialNumber }; $output += "`n=== BIOS SERIAL ==="; $output += (Get-CimInstance Win32_BIOS).SerialNumber; $output += "`n=== CPU ID ==="; $output += (Get-CimInstance Win32_Processor).ProcessorId; $output += "`n=== MAC ADDRESS ==="; Get-CimInstance Win32_NetworkAdapterConfiguration | Where-Object { $_.IPEnabled -eq $true } | ForEach-Object { $output += $_.MACAddress }; $output | Set-Content -Path $OutputPath -Encoding UTF8; Start-Process $OutputPath
